module.exports = function(grunt) {

  grunt.initConfig({
    watch: {
      sass: {
        files: ['**/*.sass'],
        tasks: ['jshint'],
        options: {
          spawn: false,
        },
      },
    },
    sass: {                                 // task
      dist: {                             // target
        files: {                        // dictionary of files
          'main.css': 'main.scss'     // 'destination': 'source'
        }
      }
    }
  });

  grunt.loadNpmTasks('grunt-sass');
  grunt.loadNpmTasks('grunt-contrib-watch');

  grunt.registerTask('default', ['sass']);

}